exports.TEMPLATE = "<div\n    [ngStyle]=\"{'top.px': top, 'left.px': left, 'width.px': width}\">\n\n    <div *ngIf=\"!multiple && filterEnabled\" class=\"filter\">\n        <input #filterInput\n               (click)=\"onSingleFilterClick()\"\n               (input)=\"onSingleFilterInput($event)\"\n               (keydown)=\"onSingleFilterKeydown($event)\">\n    </div>\n\n    <div class=\"options\" #optionsList>\n        <ul (wheel)=\"onOptionsWheel($event)\">\n            <li *ngFor=\"let option of optionList.filtered\"\n                [ngClass]=\"{'highlighted': option.highlighted, 'selected': option.selected, 'disabled': option.disabled, 'inactive': option.inactive}\"\n                (click)=\"onOptionClick(option)\"\n                (mouseover)=\"onOptionMouseover(option)\">\n                {{option.label}}\n            </li>\n            <li *ngIf=\"!optionList.hasShown\" class=\"message\">\n                {{notFoundMsg}}\n            </li>\n            <li *ngIf=\"optionList.hasMaxDisplayedOptions\" class=\"max-message\">\n                {{maxDisplayedOptionsMessage}}\n            </li>\n        </ul>\n    </div>\n</div>\n";
